import { BrowserRouter, Routes, Route } from "react-router-dom";
import React, { useState, useEffect } from "react";
import Home from "./components/Home"
import Shop from "./components/Shop"
import NavBar from "./components/NavBar"
import Cart from "./components/Cart"
import blackImg from "./components/images/blackTea.jpg"
import greenImg from "./components/images/greentea.jpg"
import gyokuroImg from "./components/images/gyokuro.jpg"
import matchImg from "./components/images/matcha.jpg"
import oolongImg from "./components/images/oolongTea.jpg"
import puerhImg from "./components/images/puerh.jpg"
import whiteImg from "./components/images/whitetea.jpg"
import yellowImg from "./components/images/yellow.jpg"
import yerbaImg from "./components/images/yerbamate.jpg"

const RouteSwitch = () => {

    function createProduct(id, name, price, details, image) {
        return {  id: id, 
          name: name,
          price: price,
          details: details,
          image: image,

        }
      }

    const yellowTea = createProduct(5, "Yellow tea", 5.99, "", yellowImg);
    const blackTea = createProduct(1, "Black tea", 6.99 , "", blackImg);
    const matchaTea = createProduct(3, "Matcha tea", 6.99, "", matchImg);
    const greenTea = createProduct(2, "Green tea", 7.99, "", );
    const whiteTea = createProduct(6, "White tea", 7.99, "", "No image");
    const yerbaTea = createProduct(4, "Yerba mate tea", 8.99, "", "No image");
    const oolongTea = createProduct(9, "Oolong tea", 8.99, "", "No image");
    const puerhTea = createProduct(7, "Pu-Erh tea", 9.99, "", "No image");
    const gyokuroTea = createProduct(8, "Gyokuro tea", 11.99, "", "No image");


    const [products, setProducts] = useState([ yellowTea, blackTea, matchaTea, greenTea, whiteTea, yerbaTea, oolongTea, puerhTea, gyokuroTea]);

    return (
        <BrowserRouter>
            <NavBar />
        <Routes>
            <Route path="/" element={<Home />}/>
            <Route path="/shop" element={<Shop products={products} setProducts={setProducts} />}/>
            <Route path="shop/cart" element={<Cart />}/>
        </Routes>
        </BrowserRouter>
    )
}

export default RouteSwitch;
