import { BrowserRouter, Routes, Route } from "react-router-dom";
import React, { useState, useEffect } from "react";
import Home from "./components/Home"
import Shop from "./components/Shop"
import NavBar from "./components/NavBar"
import Cart from "./components/Cart"


const RouteSwitch = () => {

    function createProduct(id, name, price, details, image) {
        return {  id: id, 
          name: name,
          price: price,
          details: details,
          image: image,

        }

      }

    const blackTea = createProduct(1, "black tea", 5, "This is a black tea", "No image");
    const brownTea = createProduct(1, "brown tea", 5, "This is a black tea", "No image");




    const [products, setProducts] = useState([]);


    return (
        <BrowserRouter>
            <NavBar />
        <Routes>
            <Route path="/" element={<Home />}/>
            <Route path="/shop" element={<Shop products={products} setProducts={setProducts} />}/>
            <Route path="shop/cart" element={<Cart />}/>
        </Routes>
        </BrowserRouter>
    )
}

export default RouteSwitch;
