import { BrowserRouter, Routes, Route } from "react-router-dom";
import React, { useState, useEffect } from "react";
import Home from "./components/Home"
import Shop from "./components/Shop"
import NavBar from "./components/NavBar"
import Cart from "./components/Cart"


const RouteSwitch = () => {

    function createProduct(id, name, price, details, image) {
        return {  id: id, 
          name: name,
          price: price,
          details: details,
          image: image,

        }
      }

    const blackTea = createProduct(1, "Black tea", 7.99 , "", "No image");
    const greenTea = createProduct(2, "Green tea", .99, "", "No image");
    const matchaTea = createProduct(3, "Matcha tea", 6.99, "", "No image");
    const yerbaTea = createProduct(4, "Yerba mate tea", 8.99, "", "No image");
    const yellowTea = createProduct(5, "Yellow tea", 5.99, "", "No image");
    const whiteTea = createProduct(6, "White tea", 7.99, "", "No image");
    const puerhTea = createProduct(7, "Pu-Erh tea", 9.99, "", "No image");
    const gyokuroTea = createProduct(8, "Gyokuro tea", 11.99, "", "No image");
    const lapsangTea = createProduct(9, "Lapsang souchong tea", 8.99, "", "No image");

    const [products, setProducts] = useState([blackTea, greenTea, matchaTea, yerbaTea, yellowTea, whiteTea, puerhTea, gyokuroTea, lapsangTea]);

    return (
        <BrowserRouter>
            <NavBar />
        <Routes>
            <Route path="/" element={<Home />}/>
            <Route path="/shop" element={<Shop products={products} setProducts={setProducts} />}/>
            <Route path="shop/cart" element={<Cart />}/>
        </Routes>
        </BrowserRouter>
    )
}

export default RouteSwitch;
