import "./styles/shop.scss"

const Shop = ({products, setProducts}) => {

    useEffect(() => {
        assignProducts();
    },[])

    function createProduct(id, name, price, details, image) {
        return {  id: id, 
          name: name,
          price: price,
          details: details,
          image: image,

        }

      }

      const blackTea = createProduct(1, "black tea", 5, "This is a black tea", "No image");

      function assignProducts() {
        let productsArr = [];
        productsArr.push(blackTea)
        setProducts(productsArr)
      }




    return (
        <div>
            {products.map(product => {
                return (
                    <p>{product.name}</p>
                )
            })}
        </div>
    )
}

export default Shop;
